import React from 'react'
import Card from './components/Card';
import Header from './components/Header'
import Drawer from './components/Drawer';


const arr = [
  {title: 'Мужские Кроссовки Nike Blazer Mid Suede', price: 12999, imageURL: './img/image-1.png'},
  {title: 'Мужские Кроссовки Nike Air Max 270', price: 8499, imageURL: './img/image-2.png'},
  {title: 'Мужские Кроссовки Nike Blazer Mid Suede', price: 10499, imageURL: './img/image-3.png'},
  {title: 'Кроссовки Puma X Aka Boku Future Rider', price: 5499, imageURL: './img/image-4.png'},
]

function App() {
  // const [count, setCount] = React.useState(0) // React.useState(0) = [0, f()]

  // const countPlus = () => {
  //   setCount(count + 1)
  // }
  // const countMinus = () => {
  //   setCount(count - 1)
  // }

  return (
  //   <center>
  //     <h1>{count}</h1>
  //     <button onClick={countPlus} id='plus'>+</button>
  //     <button onClick={() => countMinus()} id='minus'>-</button>
  //   </center>
    <div classNameName="App">
      <div className="wrapper">
        <Drawer/>
        <Header/>
        <div className="section">
          <div className="section-top">
              <div className="title">Все кроссовки</div>
              <div className="section-top__search">
                <input type="text"/>
              </div>
          </div>
          <div className="section-bottom">
              <div className="section-bottom__row">
                {arr.map((obj, index = 1) => (
                  <Card title={obj.title} price={obj.price} image={obj.imageURL} key={index++}/>
                ))}
              </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
