import { createSlice } from '@reduxjs/toolkit';
import { RootState } from '../store';

const initialState = {
  isOpen: false,
  isOrder: false,
};

export const basketOpenSlice = createSlice({
  name: 'basketOpen',
  initialState: initialState,
  reducers: {
    setIsOpen: (state, action) => {
      state.isOpen = action.payload;
    },
    setIsOpen: (state, action) => {
      state.isOpen = action.payload;
    },
  },
});

export const { setIsOpen } = basketOpenSlice.actions;
export const selectBasketOpen = (state: RootState) => state.basketOpen;
export default basketOpenSlice.reducer;
