import { createAsyncThunk, createSlice, PayloadAction } from '@reduxjs/toolkit';
import axios from 'axios';
import { RootState } from '../store';



export type productType = {
  id: string;
  productId: number;
  title: string;
  imageURL: string;
  text: string;
  price: number
}

export enum Status {
  LOADING = 'loading',
  FULFILLED = 'success',
  REJECTED = 'error',
}

interface ProductState {
  basketItems: productType[];
  basketStatus: Status.LOADING | Status.FULFILLED | Status.REJECTED
}
export const fetchProductBasket = createAsyncThunk<productType[]>('basketSlice/fetchProductBasket', async () => {
  const { data } = await axios.get<productType[]>('https://62b1bba0196a9e98703c1172.mockapi.io/card');
  return data;
});

export const addItems = createAsyncThunk<productType[]>('basketSlice/fetchProductBasket', async (params) => {
  const obj = params
  await axios.post('https://62b1bba0196a9e98703c1172.mockapi.io/card', obj);
  const { data } = await axios.get<productType[]>('https://62b1bba0196a9e98703c1172.mockapi.io/card');
  return data;
});
export const delItems = createAsyncThunk<productType[]>('basketSlice/fetchProductBasket', async (params) => {
  const { id: string } = params
  await axios.delete(`https://62b1bba0196a9e98703c1172.mockapi.io/card/${id}`);
  const { data } = await axios.get<productType[]>('https://62b1bba0196a9e98703c1172.mockapi.io/card');
  return data;
});
const initialState: ProductState = {
  basketItems: [],
  basketStatus: Status.LOADING,
};

export const ProductSlice = createSlice({
  name: 'product',
  initialState: initialState,
  reducers: {
    setItems: (state, action) => {
      state.basketItems = action.payload;
    },
    // addItems: (state, action) => {
    //   const obj = action.payload;
    //   axios.post('https://62b1bba0196a9e98703c1172.mockapi.io/card', obj);
    //   state.basketItems.push(obj)
    // },
    // delItems: (state, action) => {
    //   const id = action.payload;
      // axios.delete(`https://62b1bba0196a9e98703c1172.mockapi.io/card/${id}`);
    //   state.basketItems = state.basketItems.filter((item) => item.id !== id);
    // },
  },
  extraReducers: (builder) => {
    builder.addCase(fetchProductBasket.fulfilled, (state, action: PayloadAction<productType[]>) => {
      state.basketItems = action.payload;
      state.basketStatus = Status.FULFILLED;
    });
    builder.addCase(fetchProductBasket.pending, (state, action) => {
      state.basketItems = [];
      state.basketStatus = Status.LOADING;
    });
    builder.addCase(fetchProductBasket.rejected, (state, action) => {
      state.basketItems = [];
      state.basketStatus = Status.REJECTED;
    });
    builder.addCase(addItems.fulfilled, (state, action: PayloadAction<productType[]>) => {
      state.basketItems = action.payload;
      state.basketStatus = Status.FULFILLED;
    });
    builder.addCase(addItems.pending, (state, action) => {
      state.basketItems = [];
      state.basketStatus = Status.LOADING;
      // state.basketItems.push(action.payload)
      // console.log('action.payload', action.payload)
    });
    builder.addCase(addItems.rejected, (state, action) => {
      state.basketItems = [];
      state.basketStatus = Status.REJECTED;
    });
    // builder.addCase(delItems.fulfilled, (state, action: PayloadAction<productType[]>) => {
    //   state.basketItems = action.payload;
    //   state.basketStatus = Status.FULFILLED;
    // });
    // builder.addCase(delItems.pending, (state, action) => {
    //   state.basketItems = [];
    //   state.basketStatus = Status.LOADING;
    // });
    // builder.addCase(delItems.rejected, (state, action) => {
    //   state.basketItems = [];
    //   state.basketStatus = Status.REJECTED;
    // });
  },
});

export const { setItems } = ProductSlice.actions;
export const selectBasket = (state: RootState) => state.basket;
export default ProductSlice.reducer;
