import React from 'react'
import Card from './components/Card';
import Header from './components/Header'
import Drawer from './components/Drawer';

function App() {
  const [items, setItems] = React.useState([])
  const [cartItems, setCartItems] = React.useState([]);
  const [isbasket, setIsbasket] = React.useState(false);
  const [searchInput, setSearchInput] = 

  React.useEffect(() => {
    fetch('https://62b1bba0196a9e98703c1172.mockapi.io/itemsSneakers').then((res) => {
    return res.json();
    }).then((json) => {
      setItems(json);
    });
  }, []);

  const openBasket = () => {
    setIsbasket(true);
  }
  const closeBasket = () => {
    setIsbasket(false);
  }

  const addToCart = (obj) => {
      setCartItems((prev) => [...prev, obj])
  }


  return (
    <div classNameName="App">
      <div className="wrapper">
        {isbasket && <Drawer cartInfo={cartItems} closeClick={closeBasket}/>}
        <Header openClick={openBasket}/>
        <div className="section">
          <div className="section-top">
              <div className="title">Все кроссовки</div>
              <div className="section-top__search">
                <input type="text"/>
              </div>
          </div>
          <div className="section-bottom">
              <div className="section-bottom__row">
                {items.map((obj, index = 1) => (
                  <Card 
                    title={obj.title} 
                    price={obj.price} 
                    image={obj.imageURL} 
                    key={index++}
                    onFavourite={() => console.log('amksmk')}
                    onPlus={(obj) => addToCart(obj)}
                  />
                ))}
              </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;





