import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react'

// Define a service using a base URL and expected endpoints
export const productApi = createApi({
  reducerPath: 'productApi',
  baseQuery: fetchBaseQuery({ baseUrl: 'https://62b1bba0196a9e98703c1172.mockapi.io' }),
  tagTypes: ['Post'],
  endpoints: (builder) => ({
    fetchAllProduct: builder.query({
      query: () => ({
         url: '/itemsSneakers'
      })
    }),
    fetchAllBasket: builder.query({
      query: () => ({
         url: '/card'
      }),
      providesTags: result => ['Post']
    }),
    createBasket: builder.query({
      query: (obj) => ({
         url: '/card',
         method: 'POST',
         body: obj, 
      }),
      invalidatesTags: ['Post'],
    }),
    updateBasket: builder.query({
      query: () => ({
         url: '/card',
         method: 'PUT',

      }),
    }),
    fetchAllFavourite: builder.query({
      query: () => ({
         url: '/favorite'
      })
    }),
  }),
})
